    <style>
        #cl::after, ul li a::after {
            background-color: red;
            content: "";
            bottom: -5px;
            height: 2px;
            left: 0;
            opacity: 1;
            position: absolute;
            transition: all .3s;
            width: 100%;
        }
        #cl{
            cursor: pointer;
            -moz-user-select: none;
            position: relative;
        }
        ._relative{
            display: flex;
            flex-direction: row;
            justify-content: space-between;
            align-items:center;
        }
    </style>
